contracts/core/OTCTrade.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title OTCTrade
 * @dev Implements a decentralized OTC trading desk with escrow functionality
 * Based on EscrowTrade by siobh9 (https://github.com/siobh9/escrow-trade)
 */
contract OTCTrade is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Order {
        address maker;
        IERC20 tokenToSell; // Token the maker is selling (zero address for native currency)
        uint256 amountToSell; // Amount of tokens to sell
        IERC20 tokenToBuy; // Token the maker wants to buy (zero address for native currency)
        uint256 amountToBuy; // Amount of tokens to buy
        bool isActive; // Whether the order is still active
        uint256 timestamp; // When the order was created
    }

    // State variables
    uint256 private nextOrderId;
    mapping(uint256 => Order) public orders;
    mapping(address => bool) public whitelistedTokens;
    uint256 public tradingFeePercent; // Fee in basis points (1/100 of 1%)

    // Events
    event OrderCreated(
        uint256 indexed orderId,
        address indexed maker,
        address tokenToSell,
        uint256 amountToSell,
        address tokenToBuy,
        uint256 amountToBuy
    );
    event OrderCancelled(uint256 indexed orderId);
    event OrderFilled(uint256 indexed orderId, address indexed taker);
    event TokenWhitelisted(address indexed token, bool status);
    event TradingFeeUpdated(uint256 newFeePercent);
    event ExtraWithdrawn(
        address indexed user,
        address indexed token,
        uint256 amount
    );

    // Errors
    error TokensCannotBeTheSame();
    error TokenNotWhitelisted();
    error OrderNotActive();
    error NotOrderMaker();
    error InsufficientDeposit();
    error InvalidAmount();
    error FeeTooHigh();
    error NoExtraToWithdraw();

    constructor(uint256 _tradingFeePercent) Ownable(msg.sender) {
        if (_tradingFeePercent > 1000) revert FeeTooHigh(); // Max 10%
        tradingFeePercent = _tradingFeePercent;
    }

    receive() external payable {}

    // Admin functions
    function setTradingFee(uint256 _newFeePercent) external onlyOwner {
        if (_newFeePercent > 1000) revert FeeTooHigh(); // Max 10%
        tradingFeePercent = _newFeePercent;
        emit TradingFeeUpdated(_newFeePercent);
    }

    function whitelistToken(address _token, bool _status) external onlyOwner {
        whitelistedTokens[_token] = _status;
        emit TokenWhitelisted(_token, _status);
    }

    // Trading functions
    function createOrder(
        IERC20 _tokenToSell,
        uint256 _amountToSell,
        IERC20 _tokenToBuy,
        uint256 _amountToBuy
    ) external payable nonReentrant returns (uint256) {
        // Validations
        if (address(_tokenToSell) == address(_tokenToBuy))
            revert TokensCannotBeTheSame();
        if (_amountToSell == 0 || _amountToBuy == 0) revert InvalidAmount();
        if (
            !whitelistedTokens[address(_tokenToSell)] ||
            !whitelistedTokens[address(_tokenToBuy)]
        ) {
            revert TokenNotWhitelisted();
        }

        // Handle deposit
        if (address(_tokenToSell) == address(0)) {
            // Native currency
            if (msg.value != _amountToSell) revert InsufficientDeposit();
        } else {
            // ERC20 token
            _tokenToSell.safeTransferFrom(
                msg.sender,
                address(this),
                _amountToSell
            );
        }

        // Create order
        uint256 orderId = nextOrderId++;
        orders[orderId] = Order({
            maker: msg.sender,
            tokenToSell: _tokenToSell,
            amountToSell: _amountToSell,
            tokenToBuy: _tokenToBuy,
            amountToBuy: _amountToBuy,
            isActive: true,
            timestamp: block.timestamp
        });

        emit OrderCreated(
            orderId,
            msg.sender,
            address(_tokenToSell),
            _amountToSell,
            address(_tokenToBuy),
            _amountToBuy
        );

        return orderId;
    }

    function cancelOrder(uint256 _orderId) external nonReentrant {
        Order storage order = orders[_orderId];
        if (!order.isActive) revert OrderNotActive();
        if (msg.sender != order.maker) revert NotOrderMaker();

        order.isActive = false;

        // Return tokens to maker
        if (address(order.tokenToSell) == address(0)) {
            Address.sendValue(payable(order.maker), order.amountToSell);
        } else {
            order.tokenToSell.safeTransfer(order.maker, order.amountToSell);
        }

        emit OrderCancelled(_orderId);
    }

    function fillOrder(uint256 _orderId) external payable nonReentrant {
        Order storage order = orders[_orderId];
        if (!order.isActive) revert OrderNotActive();

        // Calculate fee
        uint256 fee = (order.amountToBuy * tradingFeePercent) / 10000;
        uint256 amountAfterFee = order.amountToBuy - fee;

        // Handle taker's token transfer
        if (address(order.tokenToBuy) == address(0)) {
            // Native currency
            if (msg.value < order.amountToBuy) revert InsufficientDeposit();

            // Send to maker
            Address.sendValue(payable(order.maker), amountAfterFee);
            // Send fee to owner
            if (fee > 0) {
                Address.sendValue(payable(owner()), fee);
            }
        } else {
            // ERC20 token
            order.tokenToBuy.safeTransferFrom(
                msg.sender,
                order.maker,
                amountAfterFee
            );
            if (fee > 0) {
                order.tokenToBuy.safeTransferFrom(msg.sender, owner(), fee);
            }
        }

        // Send maker's tokens to taker
        if (address(order.tokenToSell) == address(0)) {
            Address.sendValue(payable(msg.sender), order.amountToSell);
        } else {
            order.tokenToSell.safeTransfer(msg.sender, order.amountToSell);
        }

        order.isActive = false;
        emit OrderFilled(_orderId, msg.sender);
    }

    // Utility functions
    function withdrawExtra(IERC20 _token) external nonReentrant {
        uint256 balance;
        if (address(_token) == address(0)) {
            balance = address(this).balance;
        } else {
            balance = _token.balanceOf(address(this));
        }

        if (balance == 0) revert NoExtraToWithdraw();

        if (address(_token) == address(0)) {
            Address.sendValue(payable(msg.sender), balance);
        } else {
            _token.safeTransfer(msg.sender, balance);
        }

        emit ExtraWithdrawn(msg.sender, address(_token), balance);
    }

    function getOrder(uint256 _orderId) external view returns (Order memory) {
        return orders[_orderId];
    }

    function getOrdersCount() external view returns (uint256) {
        return nextOrderId;
    }
}

